import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function ArticleView() {
  const { id } = useParams();
  const [article, setArticle] = useState(null);
  const [authorName, setAuthorName] = useState('');
  const [commentContent, setCommentContent] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:8000/api/articles/${id}`)
      .then(res => setArticle(res.data))
      .catch(err => console.error(err));
  }, [id]);

  const submitComment = (e) => {
    e.preventDefault();
    axios.post(`http://localhost:8000/api/articles/${id}/comments`, {
      author_name: authorName,
      content: commentContent
    }).then(res => {
      setArticle({
        ...article,
        comments: [...article.comments, res.data]
      });
      setAuthorName('');
      setCommentContent('');
    });
  };

  if (!article) return <p>Загрузка...</p>;

  return (
    <div>
      <h2>{article.title}</h2>
      <small>{new Date(article.created_at).toLocaleDateString()}</small>
      <p>{article.content}</p>

      <h3>Комментарии</h3>
      <ul>
        {article.comments.map(c => (
          <li key={c.id}>
            <b>{c.author_name}:</b> {c.content}
          </li>
        ))}
      </ul>

      <form onSubmit={submitComment}>
        <h4>Добавить комментарий</h4>
        <input
          placeholder="Имя"
          value={authorName}
          onChange={e => setAuthorName(e.target.value)}
        />
        <br />
        <textarea
          placeholder="Комментарий"
          value={commentContent}
          onChange={e => setCommentContent(e.target.value)}
        />
        <br />
        <button type="submit">Отправить</button>
      </form>
    </div>
  );
}

export default ArticleView;
